mapper.insert:
insert into MEMBER ( username
                 , password
                 , role
                 , create_at
                 , email
                 , tel_number
)
values(
        #{username}
      , #{password}
      , #{role}
      , NOW()
      , #{email}
      , #{telNumber}
      )

mapper.update:
update MEMBER
set
    delete_yn = #{deleteYn}
  , delete_at = now()
where 1=1
and email = #{email}

mapper.select:
SELECT uid FROM MEMBER
WHERE 1 = 1
AND email = #{userEmail}
